// src/pages/ProductDetailsPage/ProductDetailsPage.js

import './productdetailspage.css';

import React, {
  useEffect,
  useState,
} from 'react';

import { useParams } from 'react-router-dom';

import images from '../../../components/';
import { ProductData } from '../../../util/ProductData';

const ProductDetailsPage = () => {
    const { id } = useParams();
    const [productData, setProductData] = useState({});

    useEffect(() => {
        let newData = ProductData.filter((product) => product.id === parseInt(id));
        setProductData(newData[0]);
    }, [id]);

    if (!productData || !productData.images) {
        return <div>Loading...</div>;
    }

    const mainImage = images[productData.images[0]];
    const smallImages = productData.images.map((image, index) => (
        <div className="small-img-col" key={index}>
            <img src={images[image]} alt={`producto pequeño ${index + 1}`} width="100%" className="small-img" />
        </div>
    ));

    return (
        <section className='product-details'>
            <div className="row mt-5">
                <div className="col-lg-5 col-md-6 col-sm-12">
                    <img className="main-img" src={mainImage} alt="producto" id="mainImg" />
                    <div className="small-img-group">
                        {smallImages}
                    </div>
                </div>

                <div className="detail col-lg-6 col-md-12 col-12">
                    <h6>{productData.category}</h6>
                    <h3 className="py-4">{productData.name}</h3>
                    <h2>{productData.price}</h2>
                    <div className="stock-info mt-4 mb-3">
                        <h6>Stock: {productData.stock} unidades</h6>
                    </div>
                    <input type="number" id="product-quantity" />
                    <button className="buy-btn">Agregar al Carrito</button>
                    <h4 className="mt-5 mb-5">Detalles del Producto</h4>
                    <span>{productData.description}</span>
                </div>
            </div>
        </section>
    );
};

export default ProductDetailsPage;
