// src/util/imageImporter.js

function importAll(r) {
  let images = {};
  r.keys().map((item, index) => { images[item.replace('./', '')] = r(item); });
  return images;
}

const images = importAll(require.context('../assets/imgs', false, /\.(png|jpe?g|svg|avif)$/));

export default images;
