import './cartpage.css';

import React, {
  useEffect,
  useState,
} from 'react';

import {
  collection,
  getDocs,
  query,
  where,
} from 'firebase/firestore';
import { useAuthState } from 'react-firebase-hooks/auth';
import { toast } from 'react-toastify';

import CartProduct from '../../components/cartproduct/CartProduct';
import Footer from '../../components/layouts/footer/Footer';
import Navbar from '../../components/layouts/navbar/Navbar';
import {
  auth,
  db,
} from '../../firebase/FireBase'; // Ruta corregida

const CartPage = () => {
  const [cartItems, setCartItems] = useState([]);
  const [user] = useAuthState(auth);
  const [total, setTotal] = useState(0);

  useEffect(() => {
    if (user) {
      fetchCartItems();
    } else {
      toast.error('Debes estar logueado para ver tu carrito');
    }
  }, [user]);

  const fetchCartItems = async () => {
    try {
      const q = query(collection(db, 'cart'), where('userId', '==', user.uid));
      const querySnapshot = await getDocs(q);
      const items = querySnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
      setCartItems(items);
      calculateTotal(items);
    } catch (error) {
      toast.error('Error al obtener los ítems del carrito');
    }
  };

  const calculateTotal = (items) => {
    const total = items.reduce((acc, item) => acc + item.price * item.quantity, 0);
    setTotal(total);
  };

  return (
    <section>
      <Navbar />
      <div className="cart container my-5 py-5">
        <div className="container mt-5">
          <h2 className="font-weight-bolde">Tu Carrito</h2>
          <hr />
        </div>
        <table className="table table-bordered my-5">
          <tbody>
            {cartItems.map((item) => (
              <CartProduct key={item.id} product={item} fetchCartItems={fetchCartItems} />
            ))}
          </tbody>
        </table>
        <div className="total">
          <h3>Total: <span>${total}</span></h3>
        </div>
      </div>
      <Footer />
    </section>
  );
};

export default CartPage;
