import './cartpage.css';

import React, {
  useEffect,
  useState,
} from 'react';

import {
  collection,
  deleteDoc,
  doc,
  getDocs,
  query,
  updateDoc,
  where,
} from 'firebase/firestore'; // Importar funciones de Firestore
import {
  useAuthState,
} from 'react-firebase-hooks/auth'; // Hook para obtener el estado de autenticación
import {
  toast,
} from 'react-toastify'; // Para mostrar notificaciones al usuario

import CartProduct from '../../components/cartproduct/CartProduct';
import {
  auth,
  db,
} from '../../components/firebase/FireBase'; // Importar la instancia de Firebase y Auth
import Footer from '../../components/layouts/footer/Footer';
import Navbar from '../../components/layouts/navbar/Navbar';

const CartPage = () => {
    const [cartItems, setCartItems] = useState([]); // Estado para almacenar los ítems del carrito
    const [user] = useAuthState(auth); // Obtener el usuario autenticado
    const [total, setTotal] = useState(0); // Estado para el total del carrito

    // Efecto para cargar los ítems del carrito al cargar la página o al cambiar el usuario autenticado
    useEffect(() => {
        if (user) {
            fetchCartItems(); // Llamar a la función para cargar los ítems del carrito si el usuario está autenticado
        } else {
            toast.error('Debes estar logueado para ver tu carrito'); // Mostrar error si el usuario no está autenticado
        }
    }, [user]); // Dependencia: se ejecutará cuando cambie el usuario autenticado

    // Función para obtener los ítems del carrito desde Firestore
    const fetchCartItems = async () => {
        try {
            const q = query(collection(db, 'cart'), where('userId', '==', user.uid)); // Consulta para obtener los ítems del carrito del usuario actual
            const querySnapshot = await getDocs(q); // Obtener el snapshot de la consulta
            const items = querySnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() })); // Mapear los documentos del snapshot a un arreglo de ítems
            setCartItems(items); // Actualizar el estado con los ítems obtenidos
            calculateTotal(items); // Calcular el total basado en los ítems del carrito
        } catch (error) {
            toast.error('Error al obtener los ítems del carrito'); // Manejar errores mostrando una notificación al usuario
        }
    };

    // Función para calcular el total del carrito
    const calculateTotal = (items) => {
        const total = items.reduce((acc, item) => acc + item.price * item.quantity, 0); // Calcular el total sumando el precio por la cantidad de cada ítem
        setTotal(total); // Actualizar el estado con el total calculado
    };

    // Función para manejar la eliminación de un ítem del carrito
    const handleRemove = async (itemId) => {
        try {
            await deleteDoc(doc(db, 'cart', itemId)); // Eliminar el documento del ítem del carrito en Firestore
            toast.success('Producto removido del carrito'); // Mostrar notificación de éxito al usuario
            fetchCartItems(); // Volver a cargar los ítems del carrito después de la eliminación
        } catch (error) {
            toast.error('Error al remover el producto del carrito'); // Manejar errores mostrando una notificación al usuario
        }
    };

    // Función para manejar la actualización de la cantidad de un ítem del carrito
    const handleUpdateQuantity = async (itemId, newQuantity) => {
        try {
            await updateDoc(doc(db, 'cart', itemId), { quantity: newQuantity }); // Actualizar el campo 'quantity' del documento en Firestore
            toast.success('Cantidad actualizada'); // Mostrar notificación de éxito al usuario
            fetchCartItems(); // Volver a cargar los ítems del carrito después de la actualización
        } catch (error) {
            toast.error('Error al actualizar la cantidad'); // Manejar errores mostrando una notificación al usuario
        }
    };

    // Función para manejar el proceso de checkout
    const handleCheckout = async () => {
        try {
            // Aquí podrías implementar la lógica para procesar el checkout,
            // como marcar los productos como comprados en tu base de datos
            // y limpiar el carrito
            const batch = db.batch(); // Iniciar una batch operation para realizar múltiples escrituras
            cartItems.forEach(item => {
                const docRef = doc(db, 'cart', item.id); // Referencia al documento del ítem del carrito
                batch.delete(docRef); // Agregar operación para eliminar el documento del ítem del carrito
            });
            await batch.commit(); // Ejecutar todas las operaciones de la batch
            toast.success('Compra realizada con éxito'); // Mostrar notificación de éxito al usuario
            setCartItems([]); // Vaciar el carrito después de realizar la compra
            setTotal(0); // Actualizar el total a cero
        } catch (error) {
            toast.error('Error al procesar el checkout'); // Manejar errores mostrando una notificación al usuario
        }
    };

    // Renderizar el componente CartPage
    return (
        <section>
            <Navbar /> {/* Componente Navbar para la barra de navegación */}
            <div className="cart container my-5 py-5">
                <div className="container mt-5">
                    <h2 className="font-weight-bold">Tu Carrito</h2> {/* Título del carrito */}
                    <hr /> {/* Línea horizontal */}
                </div>
                <table className="mt-5 pt-5">
                    <thead>
                        <tr>
                            <th>Producto</th> {/* Encabezado de la columna Producto */}
                            <th>Cantidad</th> {/* Encabezado de la columna Cantidad */}
                            <th>Subtotal</th> {/* Encabezado de la columna Subtotal */}
                        </tr>
                    </thead>
                    <tbody>
                        {/* Mapear los ítems del carrito para mostrar cada CartProduct */}
                        {cartItems.map(item => (
                            <CartProduct
                                key={item.id} // Clave única para cada ítem del carrito
                                product={item} // Propiedad product con la información del ítem del carrito
                                handleRemove={handleRemove} // Función para manejar la eliminación del ítem del carrito
                                handleUpdateQuantity={handleUpdateQuantity} // Función para manejar la actualización de la cantidad del ítem del carrito
                            />
                        ))}
                    </tbody>
                </table>
                <div className="cart-total">
                    <table>
                        <tbody>
                            <tr>
                                <td>Subtotal</td> {/* Etiqueta Subtotal */}
                                <td>${total}</td> {/* Mostrar el total calculado del carrito */}
                            </tr>
                            <tr>
                                <td>Total</td> {/* Etiqueta Total */}
                                <td>${total}</td> {/* Mostrar el total calculado del carrito */}
                            </tr>
                        </tbody>
                    </table>
                </div>
                <div className="checkout-container">
                    <button onClick={handleCheckout} className="btn checkout-btn">Continuar</button> {/* Botón para iniciar el proceso de checkout */}
                </div>
            </div>
            <Footer /> 
        </section>
    );
}

export default CartPage;
